var execSync = require('child_process').execSync;

var currentProcessStdout = '';

currentProcessStdout = execSync('backstop test', {encoding: 'utf8'});

console.log('currentProcessStdout >', currentProcessStdout);

currentProcessStdout = execSync('backstop test --config=backstop_production', {encoding: 'utf8'});

console.log('currentProcessStdout >', currentProcessStdout);
